from SernaApi import *

class HelloWorld(DocumentPlugin):
    """This is a "Hello World" Serna python plugin example."""

    def __init__(self, a1, a2):
        DocumentPlugin.__init__(self, a1, a2)
        self.buildPluginExecutors(True)

    def executeUiEvent(self, evName, cmd):
        if evName == "helloWorldMsgEvent":
            self.sernaDoc().showMessageBox(self.sernaDoc().MB_INFO,
                                           "Title",
                                           "Hello, World!",
                                           "OK")

def aboutToSave(self): self.checkEmptyParas()
def executeUiEvent(self, evName, cmd): """Execute the plugin's events. """ # Call this method on any evName, because we have # only one event in this example anyway. self.checkEmptyParas()
def checkEmptyParas(self): document = self.sernaDoc().structEditor().sourceGrove().document() node_set = XpathExpr("//para[not(node())]\ [not(self::processing-instruction('se:choice'))]").\ eval(document).getNodeSet() if node_set.size(): self.sernaDoc().messageView().clearMessages() for n in node_set: self.sernaDoc().messageView().\ emitMessage("Empty <para> element found!", n)

section.programlisting:
def executeUiEvent(self, evName, uiAction):
    if "InsertAddress" == evName:
        self.showDialog()
        return

    if "OkAddress" == evName:
        self.acceptAddress()

    # In both cases of OK and Cancel close the dialog
    self.__dialog.remove()
    # Set input focus back to the document edit window
    self.se.grabFocus()

def acceptAddress(self): """Execute event that inserts the values from dialog when user presses OK button. """
# Firstly, remove the old <address> if exists node_set = XpathExpr("//address\ [not(self::processing-instruction('se:choice'))]").eval(self.doc).getNodeSet() if node_set.firstNode(): self.se.executeAndUpdate( self.se.groveEditor().removeNode(node_set.firstNode()));
# Build element tree, taking text from the dialog, # and insert to the document. address = ["street", "city", "state", "zip", "country"] fragment = GroveDocumentFragment() address_element = GroveElement("address") fragment.appendChild(address_element)
for i in address: text = self.__dialog.findItemByName(i + "LineEdit").get("text") address_child = GroveElement(i) if len(text): address_child.appendChild(GroveText(text)) address_element.appendChild(address_child)
# Find the position for the new <address> node. # This is either right after <title> and <date> elements if # they exist, or this is the first child of the document. node_set = XpathExpr("/*/title|/*/date").eval(self.doc).getNodeSet() if node_set.size(): position_node = node_set.list()[-1] else: position_node = None if position_node: grove_pos = GrovePos(position_node.parent(), position_node.nextSibling()) else: grove_pos = GrovePos(self.doc.documentElement(), self.doc.documentElement().firstChild())
self.se.executeAndUpdate(self.se.groveEditor().paste(fragment, grove_pos))

